sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    //Create a variable for a new GlideRecord on the servicing table
    var serviceGr = new GlideRecord('x_1143021_loaner_v_vehicle_servicing');
    // Query the servicing table for the current sys_id of the record that is in the vehicle tracker field
    serviceGr.addQuery('vehicle_tracker'), current.sys_id;
    //Query the servicing table for records where the tracker status is open
    serviceGr.addQuery('ticket_status', 'Open');
    /*Create an "If statement": if no matching records are found from the previous query move on with the script: .next() */
    grVariable.query();
    if(grVariable.next()){
    }

    //Create a variable for a new GlideRecord on the servicing table
    var newServicingGr = new GlideRecord('x_1143021_loaner_v_vehicle_servicing');
    /*Create a variable for a new GlideDate that will get the current date (this will be used to se the current date in the servicing ticket's opening date) */
    var currentDate = new GlideDate();
    /*Initialize the creation of a new record on the servcing table using the GlideRecord variable you created */
    newServicingGr.initialize();
    //*Populate the required fields to create the new ticket per the requirement in the story
    //Set the value of vehicle tracker to the current records sys_id
    newServicingGr.setValue('vehicle_tracker', current.sys_id);
    //Set the value of vehicle to the current records vehicle value
    newServicingGr.setValue('reference', current.vehicle);
    //Set the value of vehicle status to in service
    newServicingGr.setValue('vehicle_status', 'In Service');
    //Set the value of the date sent for servicing to the current date
    newServicingGr.setValue('date_sent_for_servicing', currentDate.getValue());
    //Set the value of ticket status to open
    newServicingGr.setValue('ticket_status', 'Open');
    newServicingGr.insert();

})(current, previous);
