catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    // Check if the form is still loading or the new value is empty
    if (isLoading || newValue === '') {
        return;
    }

    // Get the values of the relevant fields
    var dateNeeded = g_form.getValue('date_needed');
    var dateToReturn = g_form.getValue('date_to_return');

    // Ensure both dates are not empty before proceeding
    if (!dateNeeded || !dateToReturn) {
        return;
    }

    // Convert date strings to Date objects for accurate comparison
    dateNeeded = new Date(dateNeeded);
    dateToReturn = new Date(dateToReturn);

    // Compare the dates
    if (dateToReturn < dateNeeded) {
        // Display an error message and clear the incorrect date
        g_form.addErrorMessage('Date to return should not be before the Date needed');
        g_form.clearValue('date_to_return');
        // Prevent form submission
        return false;
    }

    // Allow form submission if date comparison passes
    return true;
}
