sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var lstgr = new GlideRecord('x_1143021_loaner_v_vehicle_servicing'); // Create a variable for a new GlideRecord on the servicing table
    lstgr.addQuery('reference', current.sys_id); // Query the servicing table for the current sys_id of the record that is in the vehicle tracker field
    lstgr.addQuery('tracker_status', 'Open'); // Query the servicing table for records where the tracker status is open
    lstgr.query(); // Execute the query

    if (!lstgr._next()) {
        //Create an "if statement": if no matching records are found from the previous query move on with the script: .next()

        var lstgr2 = new GlideRecord('x_1143021_loaner_v_vehicle_servicing'); // Create a variable for a new GlideRecord on the servicing table

        var currgd = new GlideRecord(); //Create a variable for a new GlideDate that will get the current date ( this will be used to set the current date in the servicing ticket's opening date)

        lstgr2.initialize();
        lstgr2.setValue('vehicle_servicing', current.sys_id); // Set the value of vehicle to the current records vehicle value
        lstgr2.setValue('reference', current.vehicle_item); // Set the value of vehicle to the current records vehicle value
        lstgr2.setValue('vehicle_status', 'In Service'); // Set the value of vehicle status to in service
        lstgr2.setValue('date_sent_for_servicing', currgd.getValue()); // Set the value of date sent for servicing to the current date
        lstgr2.setValue('ticket_status', 'Open'); //Set the value of ticket status to open
        lstgr2.insert(); //Insert the new record

    }


})(current, previous);
