sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var trkstat = current.vehicle_tracker; //Create a variable for the current sys_id of the vehicle tracker record
    if (trkstat) { //Create an "if statement" for the variable previously created}
        var vtgr = new GlideRecord('x_1143021_loaner_v_vehicle_servicing'); //Create a variable for a new GlideRecord on the vehicle tracker table
        if (vtgr.get(trkstat)) { //Create an "if statement" on the vehicle tracker gliderecord to get the current vehicle tracker record
            vtgr.setValue('vehicle_status', current.veh_status); //Set the value of the vehicle status field on the GlideRecord to the current vehicle status on the servicing table
            vtgr.setValue('tracker_status', 'Closed'); // Set the value of the tracker status field to closed on the GlideRecord

            vtgr.update(); //Update the GlideRecord
        }

    }

})(current, previous);
